$params) {        $minfpayment = $params->get('minfpayment');        $maxfpayment = $params->get('maxfpayment');        $deffpayment = $params->get('deffpayment');        $mcDown[] = JHTML::_('select.option', $minfpayment, $minfpayment . JText::_('%'), 'value', 'text');        $minfpayment = $minfpayment + 5;        for ($i = $minfpayment; $i < $maxfpayment; $i+=5) {            $mcDown[] = JHTML::_('select.option', $i, $i . JText::_('%'));        }        $mcdowns = JHTML::_('select.genericlist', $mcDown, 'mcDown', 'class="inputbox span2" size="1" width="150" style="width: 150px"  ', 'value', 'text', $deffpayment);        return $mcdowns;    }    public static function getCurrencyname() {        $db = JFactory::getDbo();        $query = $db->getQuery(true);        $query->select('cname');        $query->from('#__expautos_currency');        $query->where('state = 1');        $query->where('exchange = 1');        $db->setQuery($query);        $currency = $db->loadResult();        return $currency;    }}?>